mapper.select:
select id, user_id, session_id, role, content, timestamp
from chat_message
where user_id = #{userId}
  and session_id = #{sessionId}
order by timestamp asc

select distinct session_id
from chat_message
where user_id = #{userId}

mapper.delete:
delete
from chat_message
where user_id = #{userId}
  and session_id = #{sessionId}
